CREATE OR REPLACE PROCEDURE update_banco_tit_pagar_escrit ( cd_banco_p bigint, nr_titulo_p bigint) AS $body$
DECLARE

qt_registros_w 	bigint;

BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (cd_banco_p IS NOT NULL AND cd_banco_p::text <> '') then
	begin
	select 	count(*)
	into STRICT	qt_registros_w
	from 	banco
	where 	cd_banco = cd_banco_p;
	if (qt_registros_w <> 0) then
		begin
		update 	titulo_pagar_escrit
		set 	cd_banco = cd_banco_p
		where 	nr_titulo = nr_titulo_p
		and 	coalesce(cd_banco::text, '') = '';
		end;
		commit;
	end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
