CREATE OR REPLACE PROCEDURE update_blood_admin_inst ( cd_cpoe_item_seq_p bigint, ie_type_action_p text) AS $body$
BEGIN

IF (cd_cpoe_item_seq_p IS NOT NULL AND cd_cpoe_item_seq_p::text <> '') THEN
    IF ( ie_type_action_p = 'U' ) THEN

        UPDATE blood_admin_instructions
        SET     cd_cpoe_item_seq = cd_cpoe_item_seq_p
        WHERE   cd_cpoe_item_seq_edit = cd_cpoe_item_seq_p
        AND     coalesce(cd_cpoe_item_seq::text, '') = '';

    ELSIF ( ie_type_action_p = 'D' ) THEN
        DELETE  FROM blood_admin_instructions
        WHERE   cd_cpoe_item_seq_edit = cd_cpoe_item_seq_p
        AND     coalesce(cd_cpoe_item_seq::text, '') = '';

    ELSIF ( ie_type_action_p = 'C' ) THEN
        DELETE  FROM blood_admin_instructions
        WHERE   cd_cpoe_item_seq_edit = cd_cpoe_item_seq_p;

    END IF;

    DELETE  FROM blood_admin_instructions
    WHERE   dt_atualizacao_nrec < clock_timestamp() - interval '1 days'
    AND     coalesce(cd_cpoe_item_seq::text, '') = '';

    COMMIT;

END IF;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
