CREATE OR REPLACE PROCEDURE update_blood_products_stock (NR_SEQ_PRODUCAO_P SAN_RESERVA_PROD.NR_SEQ_PRODUCAO%type, NR_SEQ_TRANSFUSAO_P SAN_TRANSFUSAO.NR_SEQUENCIA%type) AS $body$
BEGIN

  update SAN_RESERVA_PROD set IE_STATUS='N' where NR_SEQ_RESERVA in (SELECT NR_SEQ_RESERVA from SAN_TRANSFUSAO
  where NR_SEQUENCIA=NR_SEQ_TRANSFUSAO_P)
and NR_SEQ_PRODUCAO=NR_SEQ_PRODUCAO_P;

commit;
END;
