SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_check_dt_desejada_item ( ds_lista_p text, nm_usuario_p text, dt_desejada_p timestamp, ie_type_p text, is_desejada_p INOUT text) AS $body$
DECLARE


ds_lista_w			text;
nr_pos_virgula_w	bigint;
nr_sequencia_w		varchar(20);
ie_desejada_w		varchar(1);


BEGIN

	ds_lista_w	:= ds_lista_p;
	is_desejada_p := 'N';

	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		nr_pos_virgula_w	:= position(',' in ds_lista_w);

		if (coalesce(nr_pos_virgula_w,0) > 0) then
			nr_sequencia_w := substr(ds_lista_w, 1,(nr_pos_virgula_w - 1));
			ds_lista_w := substr(ds_lista_w,(nr_pos_virgula_w + 1), length(ds_lista_w));

			if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '')then

				if (ie_type_p = 'U') then
					update	agenda_integrada_item
					set		dt_desejada = dt_desejada_p,
							dt_atualizacao = clock_timestamp(),
							nm_usuario = nm_usuario_p
					where   nr_sequencia = (nr_sequencia_w)::numeric;
				else
					select	coalesce(max('S'),'N')
					into STRICT 	ie_desejada_w
					from	agenda_integrada_item
					where   nr_sequencia = (nr_sequencia_w)::numeric
					and		coalesce(dt_desejada::text, '') = '';

					if (ie_desejada_w = 'S') then
						is_desejada_p := 'S';
					end if;
				end if;

			end if;
        else
            ds_lista_w := null;
		end if;

	end loop; 	

	if (ie_type_p = 'U') then
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
