SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_choose_delivery (nr_sequencia_p bigint, ie_delivery_type_p text, ie_document_attachment_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	update 	rl_rep_to_hospital
	set 	ie_delivery_type = ie_delivery_type_p,
		ie_document_attachment = ie_document_attachment_p
	where 	nr_sequencia = nr_sequencia_p;
	
	update	rl_history
	set	ie_delivery_classification = case ie_delivery_type_p
	when '0' then '1'
	when '1' then '2'
	when '2' then '3'
	when '3' then '4'
	else null
	end 
where 	nr_seq_rl_rep_to_hosp = nr_sequencia_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
