CREATE OR REPLACE PROCEDURE update_cirurgia_agente_anest ( nr_cirurgia_p cirurgia_agente_anestesico.nr_cirurgia%type, nr_seq_pepo_p cirurgia_agente_anestesico.nr_seq_pepo%type) AS $body$
DECLARE

							
c01 CURSOR FOR
SELECT b.nr_seq_mat_cpoe, b.nr_seq_dieta_cpoe, a.nr_sequencia, b.nr_prescricao
	from
	cirurgia_agente_anestesico a,
	prescr_material b,
	prescr_material_compl c
	where ((a.nr_cirurgia	= nr_cirurgia_p and coalesce(nr_seq_pepo_p, 0) = 0)
		or (coalesce(a.nr_cirurgia, 0) = 0 and a.nr_seq_pepo = nr_seq_pepo_p))
	and coalesce(coalesce(a.nr_seq_mat_cpoe, a.nr_seq_diet_cpoe)::text, '') = ''
	and (coalesce(b.nr_seq_mat_cpoe, b.nr_seq_dieta_cpoe) IS NOT NULL AND (coalesce(b.nr_seq_mat_cpoe, b.nr_seq_dieta_cpoe))::text <> '')
	and b.nr_prescricao = c.nr_prescricao
	and b.nr_sequencia = c.nr_sequencia
	and a.nr_sequencia = c.nr_seq_agente_anestesico
	and a.ie_tipo in (1,2,3)
	and coalesce(a.dt_inativacao::text, '') = ''
	and cpoe_regra_cirurgia(a.cd_material) <> 'X';

BEGIN

for c01_w in c01
loop

	update cirurgia_agente_anestesico
		set nr_seq_mat_cpoe		= c01_w.nr_seq_mat_cpoe,
			nr_seq_diet_cpoe	= c01_w.nr_seq_dieta_cpoe
	where nr_sequencia = c01_w.nr_sequencia;
	commit;
	
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
