CREATE OR REPLACE PROCEDURE update_classe_mat_from_mims ( cd_classe_material_w classe_material.cd_classe_material%type, cd_imp_material_p imp_material.cd_material%type, nm_usuario_p text ) AS $body$
DECLARE

imp_classe_material_w imp_classe_material%ROWTYPE;

BEGIN

select	a.*
into STRICT	imp_classe_material_w 
from   	imp_classe_material a, 
	imp_material b 
where	b.cd_material = cd_imp_material_p 
and 	b.cd_classe_material = a.cd_classe_material;
--and 	a.ie_dirty_check = 1; 
if (imp_classe_material_w.cd_classe_material IS NOT NULL AND imp_classe_material_w.cd_classe_material::text <> '') then
	
	update	classe_material a 
	set    	a.ds_classe_material = imp_classe_material_w.ds_classe_material, 
		a.ie_situacao = imp_classe_material_w.ie_situacao, 
		a.dt_atualizacao = clock_timestamp(), 
		a.nm_usuario = nm_usuario_p,  
		a.ie_tipo_classe = imp_classe_material_w.ie_tipo_classe, 
		a.nr_seq_apres = imp_classe_material_w.nr_seq_apres, 
		a.cd_sistema_ant = imp_classe_material_w.cd_sistema_ant 
	where 	a.cd_classe_material = cd_classe_material_w;
	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
