CREATE OR REPLACE PROCEDURE update_comunic_externa_dest ( nr_seq_comunicado_p bigint, nr_seq_local_p bigint, nm_usuario_p text) AS $body$
BEGIN

	update  comunic_externa_dest a
	set     ie_enviar_email = 'N',
		nm_usuario = nm_usuario_p
	where   a.nr_seq_comunicado = nr_seq_comunicado_p
	and     a.cd_pessoa_fisica in (	SELECT	b.cd_pessoa_fisica
					from	man_pessoa_localizacao b
					where	b.nr_seq_localizacao = nr_seq_local_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
