CREATE OR REPLACE PROCEDURE update_dt_inicial_etapa ( nr_sequencia_p bigint, nr_dia_inicial_p bigint default null, nr_dia_final_p bigint default null) AS $body$
DECLARE


nm_usuario_w usuario.nm_usuario%type;


BEGIN

nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

update PROTOCOLO_INTEGRADO_ETAPA
set nr_dia_inicial = coalesce(nr_dia_inicial_p, nr_dia_inicial),
nr_dia_final = coalesce(nr_dia_final_p, nr_dia_final),
nr_dias_etapa = coalesce(nr_dia_final_p, nr_dia_final) - coalesce(nr_dia_inicial_p, nr_dia_inicial),
dt_atualizacao = clock_timestamp(),
nm_usuario = nm_usuario_w
where nr_sequencia = nr_sequencia_p;

commit;

end;
