CREATE OR REPLACE PROCEDURE update_escala_eif_itens ( nr_seq_escala_p bigint, ds_lista_itens_p text, ie_lista_result_p text) AS $body$
DECLARE


ds_lista_itens_w	varchar(2000) := '';
ds_lista_result_w	varchar(2000) := '';
ds_lista_aux_w	varchar(2000) := '';
nr_seq_item_ww	varchar(10);
nr_seq_result_ww	varchar(1);
pontuacao_regra_w	bigint;
k	integer;
m	integer;


BEGIN

ds_lista_itens_w := ds_lista_itens_p;
ds_lista_result_w := ie_lista_result_p;

while (ds_lista_itens_w IS NOT NULL AND ds_lista_itens_w::text <> '') loop

	begin
	
		select	position(',' in ds_lista_itens_w),
			position(',' in ds_lista_result_w)
		into STRICT	k,
			m
		;
		
		if (k > 1) and ((substr(ds_lista_itens_w, 1, k -1) IS NOT NULL AND (substr(ds_lista_itens_w, 1, k -1))::text <> '')) then	
		ds_lista_aux_w := substr(ds_lista_itens_w, 1, k -1);
		nr_seq_item_ww := replace(ds_lista_aux_w, ',','');
		ds_lista_itens_w := substr(ds_lista_itens_w, k + 1, 2000);
		
		elsif (ds_lista_itens_w IS NOT NULL AND ds_lista_itens_w::text <> '') then	
		nr_seq_item_ww := replace(ds_lista_itens_w,',','');
		ds_lista_itens_w := '';
		end if;
		
		if (m > 1) and ((substr(ds_lista_result_w, 1, m -1) IS NOT NULL AND (substr(ds_lista_result_w, 1, m -1))::text <> '')) then	
		ds_lista_aux_w := substr(ds_lista_result_w, 1, m -1);
		nr_seq_result_ww := replace(ds_lista_aux_w, ',','');
		ds_lista_result_w := substr(ds_lista_result_w, m + 1, 2000);
		
		elsif (ds_lista_result_w IS NOT NULL AND ds_lista_result_w::text <> '') then	
		nr_seq_result_ww := replace(ds_lista_result_w,',','');
		ds_lista_result_w := '';
		end if;
		
		update	escala_eif_item
		set 	ie_resultado = nr_seq_result_ww
		where 	nr_sequencia = nr_seq_item_ww
		and   	nr_seq_escala = nr_seq_escala_p;
		
	end;
end loop;

select  max(b.nr_sequencia)
into STRICT	pontuacao_regra_w
from    escala_eif a,
        eif_escala_result b
where   a.nr_seq_escala = b.nr_seq_escala
and     a.nr_sequencia = nr_seq_escala_p
and     b.ds_resultado = (SELECT  substr(obter_resultado_escala_eif(nr_seq_escala_p,'D'),1,255) );

if (pontuacao_regra_w IS NOT NULL AND pontuacao_regra_w::text <> '') then
	update 	escala_eif a
	set 	a.nr_seq_result = pontuacao_regra_w
	where	a.nr_sequencia = nr_seq_escala_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
