CREATE OR REPLACE PROCEDURE update_exam_result_date_pepa ( nr_seq_exame_p exame_lab_result_item.nr_seq_exame%TYPE, nr_seq_resultado_p exame_lab_result_item.nr_seq_resultado%TYPE, dt_result_item_p exame_lab_result_item.dt_result_item%TYPE ) AS $body$
DECLARE

    dt_result_item_max_w exame_lab_result_item.dt_result_item%TYPE;

BEGIN
    UPDATE exame_lab_result_item
    SET
        dt_result_item = dt_result_item_p
    WHERE
        nr_seq_exame IN (WITH RECURSIVE cte AS (

            SELECT
                nr_seq_exame
            FROM
                exame_laboratorio WHERE nr_seq_exame = nr_seq_exame_p

  UNION ALL

            SELECT
                nr_seq_exame
            FROM
                exame_laboratorio JOIN cte c ON (c.nr_seq_exame = nr_seq_superior)

) SELECT * FROM cte;
)
        AND nr_seq_resultado = nr_seq_resultado_p
        AND nr_seq_exame != nr_seq_exame_p;

    SELECT
        MAX(dt_result_item)
    INTO STRICT dt_result_item_max_w
    FROM
        exame_lab_result_item
    WHERE
        nr_seq_resultado = nr_seq_resultado_p;

    IF (dt_result_item_max_w IS NOT NULL AND dt_result_item_max_w::text <> '') THEN
        UPDATE exame_lab_resultado SET
            dt_resultado = dt_result_item_max_w
        WHERE
            nr_seq_resultado = nr_seq_resultado_p;

    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
