nr_sequencia_lista_w		varchar(2000);
nr_sequencia_w			bigint;
nr_pos_virgula_w	bigint;


BEGIN

if (nr_sequencia_lista_p IS NOT NULL AND nr_sequencia_lista_p::text <> '') then
	nr_sequencia_lista_w	:= nr_sequencia_lista_p;
	while (nr_sequencia_lista_w IS NOT NULL AND nr_sequencia_lista_w::text <> '') loop
	begin
    nr_pos_virgula_w	:= position(',' in nr_sequencia_lista_w);
		if (nr_pos_virgula_w > 0) then
			nr_sequencia_w		:= substr(nr_sequencia_lista_w,0,nr_pos_virgula_w-1);
			nr_sequencia_lista_w	:= substr(nr_sequencia_lista_w,nr_pos_virgula_w+1,length(nr_sequencia_lista_w));
		else
			nr_sequencia_w		:= (nr_sequencia_lista_w)::numeric;
			nr_sequencia_lista_w := null;
		end if;

		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			update 	AGENDA_CONSULTA set CD_MEDICO = cd_medico_p 
			where 	nr_sequencia = nr_sequencia_w;
		end if;
		end;
	end loop;
	commit;
 end if;

end;
