CREATE OR REPLACE PROCEDURE update_fechar_auditoria_js ( nr_sequencia_p bigint, ie_fechar_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_fechar_p = 'S') then
	begin
	update	qua_audit_prontuario
	set	dt_fechamento	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
	commit;
	end;

elsif (ie_fechar_p = 'N') then
	begin
	update	qua_audit_prontuario
	set	dt_fechamento	 = NULL,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
