CREATE OR REPLACE PROCEDURE update_invoice_unit_price ( DT_VALIDITY_START_P timestamp, DT_INVOICE_START_P timestamp, CD_MATERIAL_P Material.cd_material%type, VL_DELIVERY_PRICE_P NOTA_FISCAL_ITEM.VL_UNITARIO_ITEM_NF%type) AS $body$
DECLARE

          
nr_sequencia_w			NOTA_FISCAL_ITEM.nr_sequencia%type;
cd_material_w			  NOTA_FISCAL_ITEM.cd_material%type;
QT_ITEM_NF_W        NOTA_FISCAL_ITEM.QT_ITEM_NF%type;
VL_TOTAL_ITEM_NF_W  NOTA_FISCAL_ITEM.VL_TOTAL_ITEM_NF%type;
VL_DESCONTO_W       NOTA_FISCAL_ITEM.VL_DESCONTO%type;
VL_LIQUIDO_W        NOTA_FISCAL_ITEM.VL_LIQUIDO%type;
PR_DESCONTO_W       NOTA_FISCAL_ITEM.PR_DESCONTO%type;

C01 CURSOR FOR
SELECT nr_sequencia from nota_fiscal where dt_entrada_saida between dt_validity_start_p and fim_dia(dt_invoice_start_p);

C02 CURSOR FOR
SELECT CD_MATERIAL, QT_ITEM_NF, PR_DESCONTO, VL_DESCONTO from NOTA_FISCAL_ITEM where NR_SEQUENCIA=nr_sequencia_w;


BEGIN
  open C01;
loop
fetch C01 into	
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
  begin
  if (VL_DELIVERY_PRICE_P IS NOT NULL AND VL_DELIVERY_PRICE_P::text <> '') then
    begin
		OPEN C02;
          LOOP
            FETCH C02 INTO cd_material_w,QT_ITEM_NF_W,PR_DESCONTO_W,VL_DESCONTO_W;
            EXIT WHEN NOT FOUND; /* apply on C02 */

            if (cd_material_w = CD_MATERIAL_P ) THEN
              VL_TOTAL_ITEM_NF_W := QT_ITEM_NF_W * VL_DELIVERY_PRICE_P;
              if (PR_DESCONTO_W != 0) THEN
                VL_DESCONTO_W := (PR_DESCONTO_W/100)*VL_TOTAL_ITEM_NF_W;
              end if;
              VL_LIQUIDO_W := VL_TOTAL_ITEM_NF_W - VL_DESCONTO_W;

              update NOTA_FISCAL_ITEM 
              set  VL_UNITARIO_ITEM_NF = VL_DELIVERY_PRICE_P,
                   VL_TOTAL_ITEM_NF = VL_TOTAL_ITEM_NF_W,
                   VL_DESCONTO = VL_DESCONTO_W,
                   VL_LIQUIDO = VL_LIQUIDO_W,
                   dt_atualizacao	= clock_timestamp()
              where CD_MATERIAL = cd_material_w and nr_sequencia = nr_sequencia_w;
            end if;
          end loop;
    close C02;
    end;
	end if;

  end;
  end loop;
close C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
