CREATE OR REPLACE PROCEDURE update_item_ordered_on_behalf ( nr_sequencia_p bigint, cd_medico_p text, ie_tipo_item_p text) AS $body$
BEGIN

  IF (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') THEN

    IF (ie_tipo_item_p = 'M' or ie_tipo_item_p = 'S') THEN

      UPDATE cpoe_material

      SET cd_medico      = cd_medico_p

      WHERE nr_sequencia = nr_sequencia_p;

    ELSE

      UPDATE cpoe_procedimento

      SET cd_medico      = cd_medico_p

      WHERE nr_sequencia = nr_sequencia_p;

  END IF;

END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
