SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_macro_antibodies ( nr_seq_laudo_p bigint, nr_seq_peca_p bigint, ds_texto_p text) AS $body$
DECLARE




C01 CURSOR FOR
	SELECT	d.nr_sequencia
	from    laudo_grupo_questao a,
		grupo_questao_laudo b,
		item_questao_laudo c,
		laudo_questao_item d
	where   a.nr_seq_laudo = nr_seq_laudo_p
	and     (( coalesce(a.nr_seq_peca,0)  = CASE WHEN nr_seq_peca_p=0 THEN coalesce(a.nr_seq_peca,0)  ELSE nr_seq_peca_p END ) or (coalesce(nr_seq_peca::text, '') = ''))
	and	a.nr_seq_grupo_questao = b.nr_sequencia
	and	c.nr_seq_grupo = b.nr_sequencia
	and	d.nr_seq_item_questao = c.nr_sequencia
	and	d.nr_seq_laudo_grupo = a.nr_sequencia
	and    	coalesce(c.ie_situacao,'A') = 'A'
	order  	by 1;


BEGIN

	for r_C01_w in C01 loop
  	begin
  		update	laudo_questao_item
		set	ds_texto = ds_texto_p
		where	nr_sequencia = r_C01_w.nr_sequencia;
  	end;
 	end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
