CREATE OR REPLACE PROCEDURE update_malignant_tumor () AS $body$
DECLARE

c01 CURSOR FOR
    SELECT CD_ICD from ICD_MALIGNANT_TUMOR_JPN WHERE IE_SITUACAO='A';
    r01_w   c01%rowtype;


BEGIN

        OPEN C01;
        LOOP
            FETCH c01 INTO r01_w;
            EXIT WHEN NOT FOUND; /* apply on C01 */
            BEGIN

            UPDATE 	CID_DOENCA
            SET	SI_MALIGNANT_TUMOR = 'S'
            WHERE	CD_DOENCA_CID = r01_w.CD_ICD;
            END;
        END LOOP;

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE update_malignant_tumor () FROM PUBLIC;

