SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_model_name_on_duplicate (nr_seq_model_p bigint) AS $body$
DECLARE


nr_seq_model_w bigint;
ds_model_name_w varchar(50);
ds_expressao_copy varchar(50);


BEGIN

SELECT obter_desc_expressao(303214, ds_expressao_br) into STRICT ds_expressao_copy
FROM dic_expressao where cd_expressao = 303214;

SELECT ds_model_name INTO STRICT ds_model_name_w
FROM workplace_my_models
WHERE nr_sequencia = nr_seq_model_p;

SELECT max(nr_sequencia) INTO STRICT nr_seq_model_w
FROM workplace_my_models;

IF (nr_seq_model_w IS NOT NULL AND nr_seq_model_w::text <> '') THEN

	UPDATE workplace_my_models
	SET ds_model_name = concat(ds_model_name_w, 
	concat(' - ', ds_expressao_copy))
	WHERE nr_sequencia = nr_seq_model_w;

COMMIT;
END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE update_model_name_on_duplicate (nr_seq_model_p bigint) FROM PUBLIC;

