CREATE OR REPLACE PROCEDURE update_nut_guidance_data ( nr_sequencia_p bigint, dt_nut_guidance_p timestamp, cd_nut_location_p bigint, cd_companion_p bigint, ds_amount_exercise_p text, cd_tipo_recomendacao_p bigint, nr_active_factor_p bigint, nr_stress_factor_p bigint, nr_std_energy_weight_p bigint, nr_energy_body_weight_p bigint, ie_status_p text ) AS $body$
DECLARE

    ie_gerar_soap_nut_guidance_w    varchar(1) := 'N';
    cd_evolucao_w                   bigint;
    cd_pessoa_fisica_w              bigint;
    nr_atendimento_w                bigint;

BEGIN
    ie_gerar_soap_nut_guidance_w := Obter_Param_Usuario(281, 1669, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo, ie_gerar_soap_nut_guidance_w);
    select nr_atendimento,
    cd_pessoa_fisica
    into STRICT nr_atendimento_w,
    cd_pessoa_fisica_w
    from nut_orientacao_list
    where nr_sequencia = nr_sequencia_p;
    if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and ie_status_p in ('A', 'F')) then

        update nut_orientacao_list
        set dt_nut_guidance = dt_nut_guidance_p,
        cd_nut_location = cd_nut_location_p,
        cd_companion = cd_companion_p,
        ds_amount_exercise = ds_amount_exercise_p,
        cd_tipo_recomendacao = cd_tipo_recomendacao_p,
        nr_active_factor = nr_active_factor_p,
        nr_stress_factor = nr_stress_factor_p,
        nr_std_energy_weight = nr_std_energy_weight_p,
        nr_energy_body_weight = nr_energy_body_weight_p,
        ie_status = 'F',
        nm_usuario = wheb_usuario_pck.get_nm_usuario,
        dt_atualizacao = clock_timestamp()
        where nr_sequencia = nr_sequencia_p;
    end if;
	if (ie_gerar_soap_nut_guidance_w = 'S') then
    cd_evolucao_w := clinical_notes_pck.gerar_soap(nr_atendimento_w, nr_sequencia_p, 'NUT_GUIDANCE', '2', 'A', 1, cd_evolucao_w, null, cd_pessoa_fisica_w);
	update nut_orientacao_list
	set cd_evolucao = cd_evolucao_w
	where nr_sequencia = nr_sequencia_p;	
    end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
