CREATE OR REPLACE PROCEDURE update_panorama_beds (cd_setor_p bigint) AS $body$
DECLARE


nr_seq_interno_w	double precision;
nm_usuario_w		usuario.nm_usuario%type;
cd_estab_w		estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT nr_seq_interno
	FROM unidade_atendimento
	WHERE cd_setor_atendimento = cd_setor_p;


BEGIN

nm_usuario_w := coalesce(wheb_usuario_pck.get_nm_usuario, 'Tasy');
cd_estab_w   := coalesce(wheb_usuario_pck.get_cd_estabelecimento, 1);

open C01;
loop
fetch C01 into
	nr_seq_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL panorama_leito_pck.atualizar_w_pan_leito(cd_estab_w,nr_seq_interno_w, nm_usuario_w);
	end;
end loop;
close C01;

END;
$body$
