CREATE OR REPLACE PROCEDURE update_prescr_procedimento_js ( vl_parametro_p text, cd_tipo_baixa_p bigint, nm_usuario_p text, nr_seq_motivo_just_p bigint, nr_prescricao_p bigint, nr_seq_proced_p bigint, dt_baixa_p text) AS $body$
