CREATE OR REPLACE PROCEDURE update_raas_lote_atend ( ds_arquivo_p text, nr_seq_lote_atend_p bigint, nm_usuario_p text, ie_estornar_p text default 'N') AS $body$
BEGIN
if (nr_seq_lote_atend_p IS NOT NULL AND nr_seq_lote_atend_p::text <> '') then

	update	raas_lote_atendimentos
	set		dt_envio = CASE WHEN ie_estornar_p='N' THEN  clock_timestamp()  ELSE null END ,
			nm_usuario_envio = CASE WHEN ie_estornar_p='N' THEN  nm_usuario_p  ELSE null END ,
			ds_arquivo = CASE WHEN ie_estornar_p='N' THEN  ds_arquivo_p  ELSE null END ,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_lote_atend_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
