CREATE OR REPLACE PROCEDURE update_score_funcoes_html5 ( nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT 	a.cd_funcao,
		a.dt_liberacao,
		a.qt_score_final
from 	funcoes_html5_maturidade a
where 	a.nr_sequencia = (select x.nr_sequencia
						from 	funcoes_html5_maturidade x
						where	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
						and		x.cd_funcao = a.cd_funcao
						and		coalesce(x.ie_situacao,'A') <> 'I');

BEGIN

for r_c01_w in c01 loop

update	funcoes_html5
set		qt_score_funcao = r_c01_w.qt_score_final,
		dt_score_funcao = r_c01_w.dt_liberacao
where	cd_funcao = r_c01_w.cd_funcao;

end loop;


commit;

end;
