CREATE OR REPLACE PROCEDURE update_sismama_cpoe_anat_pat ( nm_usuario_p text, ie_acao_p text, nr_seq_cpoe_anat_pat_p bigint, nr_seq_sismama_p bigint) AS $body$
DECLARE

			
nr_seq_sismama_w 	sismama_atendimento.nr_sequencia%type := 0;
			

BEGIN

if ('I' = ie_acao_p) then

    if (nr_seq_sismama_p IS NOT NULL AND nr_seq_sismama_p::text <> '') then
        update sismama_atendimento 
        set nr_seq_cpoe_anatomia = nr_seq_cpoe_anat_pat_p,
        nm_usuario = nm_usuario_p,
        dt_atualizacao = clock_timestamp()	
        where nr_sequencia = nr_seq_sismama_p;
    end if;

end if;

if ('D' = ie_acao_p) then

	select max(nr_sequencia)
	  into STRICT nr_seq_sismama_w
	  from sismama_atendimento
	 where nr_seq_cpoe_anatomia = nr_seq_cpoe_anat_pat_p;
	
	 if (nr_seq_sismama_w IS NOT NULL AND nr_seq_sismama_w::text <> '') then
		update sismama_atendimento
   		   set nr_seq_cpoe_anatomia  = NULL
		 where nr_sequencia = nr_seq_sismama_w;
		
		CALL sismama_estornar_liberacao(nr_seq_sismama_w, wheb_usuario_pck.get_nm_usuario);
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
