CREATE OR REPLACE PROCEDURE update_situacao_obs_geral (nr_sequencia_p bigint, ie_um_reg_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w bigint;
ie_um_reg_w    varchar(1);
nm_usuario_w   varchar(15);


BEGIN
  nr_sequencia_w := nr_sequencia_p;
  ie_um_reg_w    := ie_um_reg_p;
  nm_usuario_w   := nm_usuario_p;

  IF (ie_um_reg_w = 'S') THEN
  BEGIN
    UPDATE prescr_proc_obs_gerais
    SET ie_situacao = 'A',
        dt_atualizacao = clock_timestamp()
    WHERE nr_sequencia = nr_sequencia_w;
  END;
  ELSE
  BEGIN
    UPDATE prescr_proc_obs_gerais
    SET ie_situacao = 'A',
        dt_atualizacao = clock_timestamp()
    WHERE nr_seq_interno = nr_sequencia_w
    AND   nm_usuario = nm_usuario_w
    AND   ie_situacao = 'I';
  END;
  END IF;

  COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
