CREATE OR REPLACE PROCEDURE update_status_ag_coletivo ( CD_PESSOA_FISICA_P text, NR_SEQ_AG_INTEGRADA_P text, NR_SEQ_AGENDA_INT_P text, IE_STATUS_P text, IE_ACTION_P text) AS $body$
DECLARE





  PACIENT RECORD;

BEGIN


      IF IE_STATUS_P <> 'FIT' OR (IE_STATUS_P = 'FIT' AND IE_ACTION_P = 'ALL') THEN

          CALL VALIDA_ITENS_AGEND_COLETIVO(NR_SEQ_AG_INTEGRADA_P);

      END IF;

            If IE_ACTION_P = 'CHECKED' THEN

                Begin

                    UPDATE AGENDAMENTO_COLETIVO

                    SET IE_STATUS_AGENDA =  CASE WHEN Ie_Status_P='TRANSFER' THEN 'T' WHEN Ie_Status_P='FIT' THEN 'E' WHEN Ie_Status_P='CANCEL' THEN 'C' END

                    WHERE CD_PESSOA_FISICA = CD_PESSOA_FISICA_P

                    AND NR_SEQ_AG_INTEGRADA = NR_SEQ_AG_INTEGRADA_P

                    AND  NR_SEQ_AGENDA_INT =  NR_SEQ_AGENDA_INT_P

                    AND (IE_STATUS_AGENDA <> 'C' OR coalesce(IE_STATUS_AGENDA::text, '') = '');

                END;



            ELSIF IE_ACTION_P = 'ALL' THEN



                BEGIN

                    FOR PACIENT IN (

                          SELECT CD_PESSOA_FISICA  FROM AGENDAMENTO_COLETIVO WHERE NR_SEQ_AG_INTEGRADA = NR_SEQ_AG_INTEGRADA_P

                        ) LOOP

                              UPDATE AGENDAMENTO_COLETIVO

                              SET IE_STATUS_AGENDA =  CASE WHEN IE_STATUS_P='TRANSFER' THEN 'T' WHEN IE_STATUS_P='FIT' THEN 'E' WHEN IE_STATUS_P='CANCEL' THEN 'C' END

                              WHERE CD_PESSOA_FISICA = PACIENT.CD_PESSOA_FISICA

                              And NR_SEQ_AG_INTEGRADA  = NR_SEQ_AG_INTEGRADA_P

                              And Nr_Seq_Agenda_Int = Nr_Seq_Agenda_Int_P

                              AND (IE_STATUS_AGENDA <> 'C' OR coalesce(IE_STATUS_AGENDA::text, '') = '');

                      END LOOP;

                END;

            END IF;



End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
