CREATE OR REPLACE PROCEDURE update_status_atend_outpatient ( nr_atendimento_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


						
/*IC - Inicio consulta
FC - Fim consulta*/
						

BEGIN

if (ie_status_p = 'IC') then
	update	atendimento_paciente
	set	dt_atend_medico		= clock_timestamp(),
		nm_usuario     		= nm_usuario_p,
		dt_atualizacao 		= clock_timestamp()
	where	nr_atendimento		= nr_atendimento_p;

elsif (ie_status_p = 'FC') then
	update	atendimento_paciente
	set	dt_fim_consulta		= clock_timestamp(),
		nm_usuario     		= nm_usuario_p,
		dt_atualizacao 		= clock_timestamp()
	where	nr_atendimento		= nr_atendimento_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
