CREATE OR REPLACE PROCEDURE update_status_frac_adep ( nr_seq_etapa_hor_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_processo_w	prescr_mat_hor.nr_seq_processo%type;

c01 CURSOR FOR
SELECT	nr_seq_processo
from	prescr_mat_hor
where	nr_sequencia in (	select	a.nr_seq_horario
							from	gedipa_etapa_hor a
							where	a.nr_sequencia 	= nr_seq_etapa_hor_p);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_processo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	adep_processo
	set		ie_status_processo = ie_status_p,
			dt_preparo = clock_timestamp(),
			nm_usuario_preparo = nm_usuario_p
	where	nr_sequencia = nr_seq_processo_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
