CREATE OR REPLACE PROCEDURE update_status_proc_wint ( ds_lista_sequencia_p text, ie_gerou_p text) AS $body$
DECLARE


nr_sequencia_w            bigint;
nr_seq_lista_w        varchar(6000);
nr_pos_virgula_w		bigint;
ds_ultimo_caracter_w        varchar(1);


BEGIN

if (ds_lista_sequencia_p IS NOT NULL AND ds_lista_sequencia_p::text <> '') then
	begin

	nr_seq_lista_w := ds_lista_sequencia_p;

	select    substr(nr_seq_lista_w, length(nr_seq_lista_w), length(nr_seq_lista_w))
	into STRICT    ds_ultimo_caracter_w
	;

	if (ds_ultimo_caracter_w <> ',') then
		nr_seq_lista_w := nr_seq_lista_w || ',';
	end if;

	while(nr_seq_lista_w IS NOT NULL AND nr_seq_lista_w::text <> '') loop
		begin

		nr_pos_virgula_w := position(',' in nr_seq_lista_w);

		if (nr_pos_virgula_w > 0) then
			begin

			nr_sequencia_w	:= substr(nr_seq_lista_w, 1, nr_pos_virgula_w - 1);
			nr_seq_lista_w	:= substr(nr_seq_lista_w, nr_pos_virgula_w + 1, length(nr_seq_lista_w));
			
			if ((nr_sequencia_w)::numeric  > 0) then
				begin

				update	procedimento_guia_wint
				set		ie_gerar = ie_gerou_p
				where	nr_sequencia = nr_sequencia_w;

				end;
			end if;
			end;
		end if;

		end;
	end loop;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
