SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_status_sp ( nm_user_p text, ie_status_p text, nr_seq_act_p bigint, nr_seq_calendar_p bigint) AS $body$
DECLARE

nr_seq_next_act_w  bigint;

BEGIN
  -- getting the next activity
  if (ie_status_p = 'E' ) then
    select q.next_seq
    into STRICT nr_seq_next_act_w
    from (SELECT nr_sequencia, nr_ordem, lead(nr_sequencia, 1) over (order by nr_ordem) as next_seq
       from status_service_pack) q   
    where q.nr_sequencia = nr_seq_act_p;
  end if;

  -- the activity do not change
  if (ie_status_p in ('I' , 'C' , 'CA', 'F' )) then
    nr_seq_next_act_w := nr_seq_act_p;
  end if;


  update  man_versao_calendario
  set    ie_status_atividade = ie_status_p,
      nm_usuario = nm_user_p,
      dt_atualizacao = clock_timestamp(),
      nr_seq_status_sp = nr_seq_next_act_w
  where  nr_sequencia = nr_seq_calendar_p;

  commit;
end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
