CREATE OR REPLACE PROCEDURE update_sur_schedule_status ( nr_seq_agenda_p bigint, ie_status_agenda_p text) AS $body$
DECLARE

					
ie_status_exists_w	varchar(1);


BEGIN

select 	coalesce(max('S'), 'N')
into STRICT 	ie_status_exists_w

where 	ie_status_agenda_p in ( 	SELECT	vl_dominio cd
									from	valor_dominio_v
									where	cd_dominio = 83
									and		obter_se_exibe_status(vl_dominio, 1, wheb_usuario_pck.get_cd_estabelecimento) = 'S');
								
if (ie_status_exists_w = 'S') then

	update 	agenda_paciente
	set 	ie_status_agenda 	= ie_status_agenda_p
	where 	nr_sequencia 		= nr_seq_agenda_p;
	
	commit;
	
end if;

end;
