SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_tc_result (nr_seq_ev_item_p bigint, ie_result_p text, nm_usuario_p text) AS $body$
DECLARE


ie_result_w		reg_tc_evidence_item.ie_result%type;
ds_exec_result_w	reg_tc_evidence_item.ds_exec_result%type;
qt_so_w			bigint;
param8_w	varchar(1);


BEGIN

	--colocar max
	select	max(ie_result),
			max(ds_exec_result)
	into STRICT	ie_result_w,
		ds_exec_result_w
	from	reg_tc_evidence_item
	where	nr_sequencia = nr_seq_ev_item_p;

	select	max(obter_valor_param_usuario(273, 8, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento))
	into STRICT	param8_w
	;

	if (param8_w <> 'S' and (ie_result_w IS NOT NULL AND ie_result_w::text <> '')) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(834385);
	end if;

	if (ie_result_p in ('F', 'B')) then
		select	count(nr_sequencia)
		into STRICT	qt_so_w
		from	reg_tc_so_pendencies
		where	nr_seq_ev_item = nr_seq_ev_item_p;

		if (qt_so_w = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(834387);
		end if;
	end if;

	update	reg_tc_evidence_item
	set	ie_result = ie_result_p,
		dt_execution = clock_timestamp(),
		nm_user_execution = nm_usuario_p
	where	nr_sequencia = nr_seq_ev_item_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
