CREATE OR REPLACE PROCEDURE update_telefone_ag_coletivo ( cd_pessoa_fisica_p text, Nr_Seq_Ag_Integrada_p bigint, Nr_Seq_Agenda_Int_P bigint, Telefone_Novo text, acao_p text default null) AS $body$
BEGIN

          if acao_p = 'LOOP' then
              Update Agendamento_Coletivo
              Set Nr_Telefone = Telefone_Novo
              Where Cd_Pessoa_Fisica = Cd_Pessoa_Fisica_P
              And Nr_Seq_Ag_Integrada = Nr_Seq_Ag_Integrada_P
              And Nr_Seq_Agenda_Int = Nr_Seq_Agenda_Int_P;

          Else

              Update Agendamento_Coletivo
              Set Nr_Telefone = (SELECT Max(Nr_Telefone_Celular) From Pessoa_Fisica Where Cd_Pessoa_Fisica = Agendamento_Coletivo.Cd_Pessoa_Fisica)
              Where coalesce(Nr_Telefone::text, '') = ''
              and Nr_Seq_Ag_Integrada = Nr_Seq_Ag_Integrada_P
              And Nr_Seq_Agenda_Int = Nr_Seq_Agenda_Int_P;

          end if;

    End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
