CREATE OR REPLACE PROCEDURE update_trans_fin_receb_panor ( ie_tipo_p text, nr_seq_caixa_rec_p cheque_cr.nr_seq_caixa_rec%TYPE ) AS $body$
DECLARE

    nr_seq_trans_cheque_w   cheque_cr.nr_seq_caixa_rec%TYPE;
    nr_seq_trans_cartao_w   movto_cartao_cr.nr_seq_caixa_rec%TYPE;

BEGIN
    SELECT
        MAX(nr_seq_trans_cheque),
        MAX(nr_seq_trans_cartao)
    INTO STRICT
        nr_seq_trans_cheque_w,
        nr_seq_trans_cartao_w
    FROM
        parametro_contas_receber
    WHERE
        cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

    IF ( ie_tipo_p = 'CH' ) THEN
        UPDATE cheque_cr
        SET
            nr_seq_trans_caixa = nr_seq_trans_cheque_w
        WHERE
            nr_seq_caixa_rec = nr_seq_caixa_rec_p;

    ELSIF ( ie_tipo_p = 'CA' ) THEN
        UPDATE movto_cartao_cr
        SET
            nr_seq_trans_caixa = nr_seq_trans_cartao_w
        WHERE
            nr_seq_caixa_rec = nr_seq_caixa_rec_p;

    END IF;

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
