CREATE OR REPLACE PROCEDURE update_treatment_data ( nr_sequencia_p rp_tratamento.nr_sequencia%TYPE, dt_inicio_tratamento_p rp_tratamento.dt_inicio_tratamento%TYPE, nr_seq_dis_classif_p rp_tratamento.nr_seq_dis_classif%TYPE, nr_seq_calc_classif_p rp_tratamento.nr_seq_calc_classif%TYPE, dt_early_addition_start_p rp_tratamento.dt_early_addition_start%TYPE, ie_information_p rp_tratamento.ie_information%TYPE ) AS $body$
DECLARE

    cd_evolucao_w            cpoe_procedimento.cd_evolucao%TYPE;

BEGIN
    UPDATE rp_tratamento
    SET
        nr_seq_dis_classif = nr_seq_dis_classif_p,
        dt_inicio_tratamento = dt_inicio_tratamento_p,
        nr_seq_calc_classif = nr_seq_calc_classif_p,
        dt_early_addition_start = dt_early_addition_start_p,
        ie_information = ie_information_p
    WHERE
        nr_sequencia = nr_sequencia_p;
    select max(a.cd_evolucao)
	into STRICT cd_evolucao_w
	from cpoe_procedimento a, rp_tratamento b
	where a.nr_sequencia = b.nr_seq_cpoe_procedimento and b.nr_sequencia = nr_sequencia_p;
    IF (coalesce(cd_evolucao_w, 0) > 0 ) THEN
        CALL clinical_notes_pck.soap_data_after_delete(cd_evolucao_w);
    END IF;
    commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
