			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
			where nr_seq_main_daily_rep = nr_seq_main_daily_rep_p;
	end if;
	
	if (nr_undo_signs_p >= 3) then
		update agreg_signatures
		set NM_USER_SIGNATURE_3  = NULL,
			dt_signature_3  = NULL,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
			where nr_seq_main_daily_rep = nr_seq_main_daily_rep_p;	
	end if;
	
	if (nr_undo_signs_p >= 4) then
		update agreg_signatures
		set NM_USER_SIGNATURE_2  = NULL,
			dt_signature_2  = NULL,
			nm_usuario = nm_usuario_p,
