SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_validacao_rxt_agenda (cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	update	rxt_agenda
	set	ie_status_agenda	= 'A',
		dt_confirmacao 		= coalesce(dt_confirmacao,clock_timestamp()),
		nm_usuario_confirmacao 	= coalesce(nm_usuario_confirmacao, nm_usuario_p),
		dt_chegada 		= coalesce(dt_chegada, clock_timestamp()),
		nm_usuario_chegada 	= coalesce(nm_usuario_chegada, nm_usuario_p)
	where (nr_seq_tratamento in (SELECT	nr_sequencia
					from 	rxt_tratamento
					where 	nr_seq_tumor in (select	nr_sequencia
								from 	rxt_tumor
								where	cd_pessoa_fisica = cd_pessoa_fisica_p))
		or cd_pessoa_fisica 	= cd_pessoa_fisica_p)
	and	trunc(dt_agenda)	= trunc(clock_timestamp());
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
