SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE update_validation_result (nr_seq_ev_item_p reg_validation_so_pend.nr_seq_validation_item%TYPE, ie_result_p text, nm_usuario_p text) AS $body$
DECLARE


ie_result_w		reg_validation_pend_item.ie_result%TYPE;
qt_so_w			bigint;


BEGIN

	if ie_result_p <> 'U' then
		select	ie_result
		into STRICT	ie_result_w
		from	reg_validation_pend_item
		where	nr_sequencia = nr_seq_ev_item_p;

		if (ie_result_w IS NOT NULL AND ie_result_w::text <> '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(834385);
		end if;
	end if;

	if (ie_result_p in ('F', 'B')) then
		select	count(nr_sequencia)
		into STRICT	qt_so_w
		from	reg_validation_so_pend
		where	nr_seq_validation_item = nr_seq_ev_item_p;

		if (qt_so_w = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(834387);
		end if;
	end if;

	update	reg_validation_pend_item
	set	ie_result = CASE WHEN ie_result_p='U' THEN  NULL  ELSE ie_result_p END ,-- U - Undo validation update
		dt_execution = clock_timestamp(),
		nm_user_execution = nm_usuario_p
	where	nr_sequencia = nr_seq_ev_item_p;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
