SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE updt_todas_guis_analizadas ( nr_seq_retorno_p bigint, ie_anailizar_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_anailizar_p = 'H') then
	begin

	update   convenio_retorno_item a
	set      dt_analisada   = clock_timestamp(),
	ie_analisada   = 'S'
	where (coalesce(a.vl_amenor,0) = 0 or a.ie_analisada = 'S' or
	not exists (SELECT  1
	     from     convenio_retorno_glosa x
	     where    coalesce(x.nr_seq_conpaci_ret_hist::text, '') = ''
	     and      x.nr_seq_ret_item          = a.nr_sequencia))
	and      a.nr_seq_retorno = nr_seq_retorno_p;

	end;

elsif (ie_anailizar_p = 'S') then
	begin

	update convenio_retorno_item
	set dt_analisada = clock_timestamp(),
	ie_analisada   = 'S'
	where nr_seq_retorno = nr_seq_retorno_p;

	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
