SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE upd_drug_interactions_mims (nr_seq_mims_ver bigint, nm_usuario_p text) AS $body$
DECLARE

	is_DrugInteract_import_w smallint := 0;

BEGIN
	SELECT coalesce(IS_DRUGINTERACTIONS_IMPORTED,0)
		INTO STRICT is_DrugInteract_import_w
		from MIMS_VERSION where NR_SEQUENCIA = nr_seq_mims_ver;
		
	if ( is_DrugInteract_import_w <> 1 ) then
		--DrugInteractions will be removed from IMPORT_MIMS_MATERIAL.PRC and invoked here
		--invoke procedure to import and update drug interactions
		CALL material_interacao_medic_ficha(nm_usuario_p);
		
		--update the status of drug interaction import
		update	mims_version a
		set	IS_DRUGINTERACTIONS_IMPORTED = 1
		where	a.nr_sequencia = nr_seq_mims_ver;
		
		commit;
		
	end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
