CREATE OR REPLACE PROCEDURE upd_mat_recao_frm_mims ( cd_imp_material_p imp_material.cd_material%type, ds_reacao_w imp_material_reacao.ds_reacao%type, nm_usuario_p text ) AS $body$
DECLARE

cd_material_val   material.cd_material%type;

BEGIN

select	max(cd_material_tasy)
into STRICT	cd_material_val 
from 	mims_material_tasy
where 	cd_imp_material = cd_imp_material_p;

update	material_reacao a
set	a.ds_reacao = ds_reacao_w, 
	a.dt_atualizacao = clock_timestamp(), 
	a.nm_usuario = nm_usuario_p 
where  	a.cd_material = cd_material_val;

/* update	imp_material_reacao a 
set    	a.ie_dirty_check = 0 
where  	a.cd_material = cd_imp_material_p;  */
exception when others then null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
