SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE upd_orientacao_usuario () AS $body$
DECLARE

	ds_orientacao_usuario_w imp_material.ds_orientacao_usuario%type;
	c01 CURSOR FOR
	SELECT * FROM imp_material where ds_orientacao_usuario like '%<SUB>%';
BEGIN
	FOR r01 in c01
	LOOP
		ds_orientacao_usuario_w := replace(replace(r01.ds_orientacao_usuario, '<SUB>', ''), '</SUB>', '');
		UPDATE imp_material a
        SET    a.ds_orientacao_usuario = ds_orientacao_usuario_w 
        WHERE  a.cd_material = r01.cd_material;
	END LOOP;
	COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
