CREATE OR REPLACE PROCEDURE upload_big_table_csv ( ds_conteudo_linha_p text, ie_tipo_linha_p text, nm_usuario_p text, nm_tabela_carga_p text) AS $body$
BEGIN

if (coalesce(ie_tipo_linha_p,'L') = 'C') then
	begin
	delete from w_load_big_table_csv
	where nm_usuario = nm_usuario_p;
	end;
end if;

insert into w_load_big_table_csv(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_conteudo_linha,
	ie_tipo_linha,
	nm_tabela_carga)
values (nextval('w_load_big_table_csv_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_conteudo_linha_p,
	coalesce(ie_tipo_linha_p,'L'),
	nm_tabela_carga_p);

commit;

end;
