CREATE OR REPLACE PROCEDURE upt_dum_agenda_pac_adic_info ( nr_seq_agenda_p text, dt_ultima_menstruacao_p timestamp) AS $body$
DECLARE


			
nr_sequencia_w		AGENDA_PACIENTE_INF_ADIC.nr_sequencia%type;
			

BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select max(nr_sequencia)
	into STRICT nr_sequencia_w
	from AGENDA_PACIENTE_INF_ADIC
	where nr_seq_agenda = nr_seq_agenda_p;
	
	if (coalesce(nr_sequencia_w::text, '') = '') then
	
		insert into AGENDA_PACIENTE_INF_ADIC(	
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
      nm_usuario_nrec,
      dt_atualizacao_nrec,
			nr_seq_agenda,
			dt_ultima_menstruacao)
		values (	nextval('agenda_paciente_inf_adic_seq'),
			clock_timestamp(),
			obter_usuario_ativo,
      obter_usuario_ativo,
      clock_timestamp(),
			nr_seq_agenda_p,
			dt_ultima_menstruacao_p);
	else
		begin

		update	AGENDA_PACIENTE_INF_ADIC
		set dt_ultima_menstruacao = dt_ultima_menstruacao_p,
        nm_usuario = obter_usuario_ativo,
        dt_atualizacao = clock_timestamp()
		where nr_seq_agenda = nr_seq_agenda_p;
		end;
	end if;
	

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
