CREATE OR REPLACE PROCEDURE ur_gerar_taxa_ano (nm_usuario_p text) AS $body$
DECLARE


nr_mes_w	integer := 1;
nr_mes_ww	varchar(2);
dt_ref_w	timestamp;
ds_ano_ref_w	varchar(4);

BEGIN

ds_ano_ref_w	:= to_char(trunc(clock_timestamp()),'yyyy');

if (coalesce(ds_ano_ref_w,0) > 1990) then

	dt_ref_w := to_date('01/01/'||substr(ds_ano_ref_w,1,4), 'dd/mm/yyyy');
	while(nr_mes_w <= 12) loop
		begin
		if	pkg_date_utils.start_of(clock_timestamp(),'MONTH',0) = dt_ref_w then
			nr_mes_w := 13;
		else
			CALL Atualizar_Resumo_Geral_Setor(dt_ref_w,nm_usuario_p);
		end if;
		nr_mes_w := nr_mes_w + 1;
		dt_ref_w := pkg_date_utils.add_month(dt_ref_w,1,0);
		end;
	end loop;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
