CREATE OR REPLACE PROCEDURE validacao_exclusao_orgao_cobr ( nr_titulo_p bigint, nr_seq_cheque_p bigint, nr_seq_cobranca_p bigint) AS $body$
DECLARE


nr_titulo_w		bigint;
nr_cheque_w		bigint;
nr_outros_w		bigint;


BEGIN

select	count(*)
into STRICT	nr_titulo_w
from	titulo_receber_orgao_cobr
where	nr_titulo = nr_titulo_p
and	nr_seq_cobranca	= nr_seq_cobranca_p;

select	count(*)
into STRICT	nr_cheque_w
from	cheque_cr_orgao_cobr
where	nr_seq_cheque = nr_seq_cheque_p
and	nr_seq_cobranca	= nr_seq_cobranca_p;

select	count(*)
into STRICT	nr_outros_w
from	outros_orgao_cobr
where	nr_seq_cobranca	= nr_seq_cobranca_p;

if (nr_titulo_w > 0) or (nr_cheque_w > 0) or (nr_outros_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(337218);
end if;

commit;

end;
$body$
