-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE validar_aprovacao_compra ( nr_seq_aprovacao_p processo_aprov_compra.nr_sequencia%type, nr_seq_processo_p processo_aprov_compra.nr_seq_proc_aprov%type, ie_origem_processo_p text, nm_usuario_p processo_aprov_compra.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

			
ie_consiste_vl_empenho_w	varchar(5);
nr_seq_mes_ref_w		ctb_mes_ref.nr_sequencia%type;
cd_conta_contabil_w		conta_contabil.cd_conta_contabil%type;
cd_centro_custo_w		centro_custo.cd_centro_custo%type;
vl_orcado_w			ctb_orcamento.vl_orcado%type;	
vl_empenho_w			ctb_orcamento.vl_empenho%type;
dt_referencia_w			ctb_mes_ref.dt_referencia%type;
ds_consistencia_w		requisicao_mat_consist.ds_consistencia%type;
nr_requisicao_w			requisicao_material.nr_requisicao%type;
nr_solic_compra_w		solic_compra.nr_solic_compra%type;
nr_ordem_compra_w		ordem_compra.nr_ordem_compra%type;


BEGIN

if (ie_origem_processo_p = 'R') then

	select	max(nr_requisicao)
	into STRICT	nr_requisicao_w
	from	item_requisicao_material
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;
	
	if (nr_requisicao_w IS NOT NULL AND nr_requisicao_w::text <> '') then

		ie_consiste_vl_empenho_w	:= coalesce(obter_valor_param_usuario( cd_funcao_p => 925, nr_sequencia_p => 124, cd_perfil_p => Obter_perfil_ativo,
									nm_usuario_p => nm_usuario_p, cd_estabelecimento_p => cd_estabelecimento_p),'N');

		if (coalesce(ie_consiste_vl_empenho_w,'N') = 'S') then
			begin
			select	r.dt_solicitacao_requisicao,
				r.cd_centro_custo
			into STRICT	dt_referencia_w,
				cd_centro_custo_w
			from	requisicao_material r
			where	r.nr_requisicao = nr_requisicao_w;
			exception
			when no_data_found or too_many_rows then
				dt_referencia_w := null;
				cd_centro_custo_w := null;
			end;

			select	max(nr_sequencia)
			into STRICT	nr_seq_mes_ref_w
			from	ctb_mes_ref
			where	trunc(dt_referencia,'month') = trunc(dt_referencia_w,'month');
			
			select	max(i.cd_conta_contabil)
			into STRICT	cd_conta_contabil_w
			from	item_requisicao_material i,
				requisicao_material r
			where	i.nr_requisicao	= nr_requisicao_w
			and	i.nr_requisicao	= r.nr_requisicao
			and (substr(obter_se_conta_vigente(i.cd_conta_contabil, r.dt_solicitacao_requisicao),1,1) = 'S');

			vl_orcado_w :=	coalesce(ctb_obter_valor_orcado( dt_referencia_p => trunc(dt_referencia_w,'month'), cd_conta_contabil_p => cd_conta_contabil_w,
									cd_centro_custo_p => cd_centro_custo_w, cd_estabelecimento_p => cd_estabelecimento_p),0);

			select	coalesce(max(vl_empenho),0)
			into STRICT	vl_empenho_w
			from	ctb_orcamento
			where	cd_estabelecimento	= cd_estabelecimento_p
			and	cd_centro_custo		= cd_centro_custo_w
			and	cd_conta_contabil	= cd_conta_contabil_w
			and	nr_seq_mes_ref		= nr_seq_mes_ref_w;
			
			if (vl_empenho_w = 0) then
				vl_empenho_w := coalesce(ctb_obter_empenho_orcamento( nr_seq_mes_ref_p => nr_seq_mes_ref_w, cd_conta_contabil_p => cd_conta_contabil_w,
									cd_centro_custo_p => cd_centro_custo_w, cd_estabelecimento_p => cd_estabelecimento_p, 
									dt_inicial_p => null, dt_final_p => null),0);
			end if;
			
			if (vl_empenho_w > vl_orcado_w) then
				ds_consistencia_w := substr(	wheb_mensagem_pck.get_texto(nr_seq_mensagem_p => 1196401, vl_macros_p => null) ||chr(10)||chr(13)||
								wheb_mensagem_pck.get_texto(nr_seq_mensagem_p => 1196644,
											vl_macros_p => 'VL_EMPENHO='|| campo_mascara_virgula(vl_empenho_w) ||
													';VL_ORCADO='|| campo_mascara_virgula(vl_orcado_w)),1,255);

				CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_consistencia_w);
			end if;
		end if;
	end if;
	
elsif (ie_origem_processo_p = 'S') then

	select	max(nr_solic_compra)
	into STRICT	nr_solic_compra_w
	from	solic_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;
	
	if (nr_solic_compra_w IS NOT NULL AND nr_solic_compra_w::text <> '') then

		ie_consiste_vl_empenho_w	:= coalesce(obter_valor_param_usuario( cd_funcao_p => 925, nr_sequencia_p => 125, cd_perfil_p => Obter_perfil_ativo,
								nm_usuario_p => nm_usuario_p, cd_estabelecimento_p => cd_estabelecimento_p),'N');

		if (coalesce(ie_consiste_vl_empenho_w,'N') = 'S') then
			begin
			select	dt_solicitacao_compra,
				cd_conta_contabil,
				cd_centro_custo
			into STRICT	dt_referencia_w,
				cd_conta_contabil_w,
				cd_centro_custo_w
			from	solic_compra
			where	nr_solic_compra = nr_solic_compra_w;
			exception
			when no_data_found or too_many_rows then
				dt_referencia_w := null;
				cd_conta_contabil_w := null;
				cd_centro_custo_w := null;
			end;

			select	max(nr_sequencia)
			into STRICT	nr_seq_mes_ref_w
			from	ctb_mes_ref
			where	trunc(dt_referencia,'month') = trunc(dt_referencia_w,'month');
				
			vl_orcado_w :=	coalesce(ctb_obter_valor_orcado( dt_referencia_p => trunc(dt_referencia_w,'month'), cd_conta_contabil_p => cd_conta_contabil_w,
									cd_centro_custo_p => cd_centro_custo_w, cd_estabelecimento_p => cd_estabelecimento_p),0);

			select	coalesce(max(vl_empenho),0)
			into STRICT	vl_empenho_w
			from	ctb_orcamento
			where	cd_estabelecimento	= cd_estabelecimento_p
			and	cd_centro_custo		= cd_centro_custo_w
			and	cd_conta_contabil	= cd_conta_contabil_w
			and	nr_seq_mes_ref		= nr_seq_mes_ref_w;
			
			if (vl_empenho_w = 0) then
				vl_empenho_w := coalesce(ctb_obter_empenho_orcamento( nr_seq_mes_ref_p => nr_seq_mes_ref_w, cd_conta_contabil_p => cd_conta_contabil_w,
									cd_centro_custo_p => cd_centro_custo_w, cd_estabelecimento_p => cd_estabelecimento_p, 
									dt_inicial_p => null, dt_final_p => null),0);
			end if;

			if (vl_empenho_w > vl_orcado_w) then
				ds_consistencia_w := substr(	wheb_mensagem_pck.get_texto(nr_seq_mensagem_p => 1196401, vl_macros_p => null) ||chr(10)||chr(13)||
								wheb_mensagem_pck.get_texto(nr_seq_mensagem_p => 1196644,
											vl_macros_p => 'VL_EMPENHO='|| campo_mascara_virgula(vl_empenho_w) ||
													';VL_ORCADO='|| campo_mascara_virgula(vl_orcado_w)),1,255);
								
				CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_consistencia_w);
			end if;
		end if;
	end if;

elsif (ie_origem_processo_p = 'O') then

	select	max(nr_ordem_compra)
	into STRICT	nr_ordem_compra_w
	from	ordem_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;
	
	if (nr_ordem_compra_w IS NOT NULL AND nr_ordem_compra_w::text <> '') then
	
		ie_consiste_vl_empenho_w	:= coalesce(obter_valor_param_usuario( cd_funcao_p => 925, nr_sequencia_p => 126, cd_perfil_p => Obter_perfil_ativo,
										nm_usuario_p => nm_usuario_p, cd_estabelecimento_p => cd_estabelecimento_p),'N');
		
		if (coalesce(ie_consiste_vl_empenho_w,'N') = 'S') then
			begin
			select	dt_ordem_compra,
				cd_centro_custo
			into STRICT	dt_referencia_w,
				cd_centro_custo_w
			from	ordem_compra
			where	nr_ordem_compra = nr_ordem_compra_w;
			exception
			when no_data_found or too_many_rows then
				dt_referencia_w := null;
				cd_centro_custo_w := null;
			end;

			select	max(nr_sequencia)
			into STRICT	nr_seq_mes_ref_w
			from	ctb_mes_ref
			where	trunc(dt_referencia,'month') = trunc(dt_referencia_w,'month');
				
			select	max(i.cd_conta_contabil),
				coalesce(max(i.cd_centro_custo),cd_centro_custo_w)
			into STRICT	cd_conta_contabil_w,
				cd_centro_custo_w
			from	ordem_compra_item i
			where	i.nr_ordem_compra	= nr_ordem_compra_w;

			vl_orcado_w :=	coalesce(ctb_obter_valor_orcado( dt_referencia_p => trunc(dt_referencia_w,'month'), cd_conta_contabil_p => cd_conta_contabil_w,
									cd_centro_custo_p => cd_centro_custo_w, cd_estabelecimento_p => cd_estabelecimento_p),0);
									
			select	coalesce(max(vl_empenho),0)
			into STRICT	vl_empenho_w
			from	ctb_orcamento
			where	cd_estabelecimento	= cd_estabelecimento_p
			and	cd_centro_custo		= cd_centro_custo_w
			and	cd_conta_contabil	= cd_conta_contabil_w
			and	nr_seq_mes_ref		= nr_seq_mes_ref_w;
									
			if (vl_empenho_w > vl_orcado_w) then
				ds_consistencia_w := substr(	wheb_mensagem_pck.get_texto(nr_seq_mensagem_p => 1196401, vl_macros_p => null) ||chr(10)||chr(13)||
								wheb_mensagem_pck.get_texto(nr_seq_mensagem_p => 1196644,
											vl_macros_p => 'VL_EMPENHO='|| campo_mascara_virgula(vl_empenho_w) ||
													';VL_ORCADO='|| campo_mascara_virgula(vl_orcado_w)),1,255);
								
				CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_consistencia_w);
			end if;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE validar_aprovacao_compra ( nr_seq_aprovacao_p processo_aprov_compra.nr_sequencia%type, nr_seq_processo_p processo_aprov_compra.nr_seq_proc_aprov%type, ie_origem_processo_p text, nm_usuario_p processo_aprov_compra.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;

