SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE validar_encerrar_episodio ( nr_seq_episodio_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			integer;
ds_conteudo_w		varchar(2000);
nr_atendimento_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_atendimento
from    atendimento_paciente a
where   a.nr_seq_episodio = nr_seq_episodio_p
and     coalesce(a.dt_alta::text, '') = '';


BEGIN

delete
from	episodio_paciente_consist
where	ie_origem = 'TASY';

select  count(a.nr_atendimento)
into STRICT    qt_existe_w
from    atendimento_paciente a
where   a.nr_seq_episodio = nr_seq_episodio_p
and     coalesce(a.dt_alta::text, '') = '';

if (qt_existe_w > 0) then
	begin

	ds_conteudo_w	:= substr(wheb_mensagem_pck.get_texto(1120552),1,50) || ':';

	open c01;
	loop
	fetch c01 into
		nr_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		ds_conteudo_w	:= ds_conteudo_w || chr(13) || nr_atendimento_w;

		end;
	end loop;
	close c01;

	insert into episodio_paciente_consist(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_episodio,
		ds_titulo,
		ds_conteudo,
		ie_tipo,
		ie_origem)
	values (
		nextval('episodio_paciente_consist_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_episodio_p,
		substr(wheb_mensagem_pck.get_texto(1093956),1,255),
		ds_conteudo_w,
		'E',
		'TASY');	
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE validar_encerrar_episodio ( nr_seq_episodio_p bigint, nm_usuario_p text) FROM PUBLIC;

