CREATE OR REPLACE PROCEDURE validar_qua_doc_revisao ( nr_sequencia_p bigint, dt_validacao_p timestamp, cd_pessoa_validacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
dt_validacao_w	timestamp;
nr_seq_doc_w	bigint;	
	 

BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (dt_validacao_p IS NOT NULL AND dt_validacao_p::text <> '') and (cd_pessoa_validacao_p IS NOT NULL AND cd_pessoa_validacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin	 
	update	qua_doc_revisao 
	set	dt_atualizacao = clock_timestamp(), 
		nm_usuario = nm_usuario_p, 
		dt_validacao = dt_validacao_p, 
		cd_pessoa_validacao = cd_pessoa_validacao_p 
	where	nr_sequencia = nr_sequencia_p;
		 
	commit;
	 
	select	coalesce(nr_seq_doc, 0) 
	into STRICT	nr_seq_doc_w 
	from	qua_doc_revisao 
	where	nr_sequencia = nr_sequencia_p;
	 
	if (nr_seq_doc_w > 0) then 
	begin 
		 
		select	dt_validacao 
		into STRICT	dt_validacao_w 
		from	qua_documento 
		where	nr_sequencia = nr_seq_doc_w;
	 
		if (coalesce(dt_validacao_w::text, '') = '') then 
			CALL qua_gerar_validacao_documento(nr_seq_doc_w, cd_pessoa_validacao_p, dt_validacao_p, nm_usuario_p);
		end if;
	end;	
	end if;
	 
	end;
end if;
 
end;
$body$
