SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE validate_user_category ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_usuario_convenio_p text, ie_clinica_p bigint, ie_param_1219 text, ie_usuario_categoria_p INOUT text, ie_regra_bloqueio_p INOUT text, ds_observacao_p INOUT text) AS $body$
DECLARE


ie_usuario_categoria_w	varchar(1)  := 'S';
ie_encontrou_registro_w varchar(1) := 'N';
ie_regra_bloqueio_w  	varchar(1) := 'X';
qt_tamanho_w			integer;
ds_observacao_w    		varchar(255);	
ie_tipo_atendimento_w  	bigint;
ie_clinica_w    		integer;
		
					

BEGIN
--Procedure utilizada para tratar o parametro 1219 junto ao 753
ie_encontrou_registro_w := 'N';
ie_tipo_atendimento_w 	:= coalesce(ie_tipo_atendimento_p,0);
ie_clinica_w    	  	:= coalesce(ie_clinica_p,0);

--Verifica se ha regra cadastrada para esse codigo de categoria convenio
select  CASE WHEN(max(nr_sequencia) > 0) THEN 'S' ELSE 'N' END ie_encontrou_registro,
			coalesce(max(ie_regra_bloqueio),'X'),
			sum(nr_pos_final - nr_pos_inicial),
			max(ds_observacao)
	into STRICT 	ie_encontrou_registro_w,
			ie_regra_bloqueio_w,
			qt_tamanho_w,
			ds_observacao_w
from 	conv_regra_usuario_categ
where  	cd_estabelecimento  = cd_estabelecimento_p
and  	cd_convenio  = cd_convenio_p
and  	cd_categoria  = cd_categoria_p
and  	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w) = ie_tipo_atendimento_w
and  	coalesce(ie_clinica, ie_clinica_w) = ie_clinica_w
AND     cd_usuario_padrao = substr(cd_usuario_convenio_p,nr_pos_inicial,(nr_pos_final - nr_pos_inicial) + 1)  LIMIT 1;

if (ie_encontrou_registro_w = 'S' and ie_param_1219 = 'S') then
-- 1219 como S - Bloquear ou avisar, somente para codigos de usuario diferentes da categoria informada
	ie_usuario_categoria_w := 'S';
end if;

if (ie_encontrou_registro_w = 'S' and ie_param_1219 = 'O' and (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '')) then
--1219 como O - Bloquear ou avisar, tambem para codigos de usuario iguais ao da categoria quando existir mensagem de observacao na regra
	ie_usuario_categoria_w := 'X';
end if;


if (ie_encontrou_registro_w <> 'S')then
--Caso nao tenha encontrado registro, e buscado em outras regras de categoria convenio esse codigo (Para o mesmo convenio)
	select  CASE WHEN(max(nr_sequencia) > 0) THEN 'S' ELSE 'N' END ie_encontrou_registro,
			coalesce(max(ie_regra_bloqueio),'X'),
			sum(nr_pos_final - nr_pos_inicial),
			max(ds_observacao)
	into STRICT 	ie_encontrou_registro_w,
			ie_regra_bloqueio_w,
			qt_tamanho_w,
			ds_observacao_w
	from 	conv_regra_usuario_categ
	where  	cd_estabelecimento  = cd_estabelecimento_p
	and  	cd_convenio  = cd_convenio_p
	and  	cd_categoria  <> cd_categoria_p
	AND     cd_usuario_padrao = substr(cd_usuario_convenio_p,nr_pos_inicial,(nr_pos_final - nr_pos_inicial) + 1)  LIMIT 1;

	
	if (ie_encontrou_registro_w = 'S') then
		--'X' Encontrou a regra porem para outra categoria e para adicionar a mensagem
		ie_usuario_categoria_w := 'X';
		ds_observacao_w := obter_desc_expressao(507889)||' '||chr(13)||ds_observacao_w;
	else
		-- Categoria tem regra, codigo de usuario informado nao coincide com a regra e nao ha outras regras cadastradas para aquele codigo, exibir msg da regra da categoria, caso msg exista
		select  CASE WHEN(max(nr_sequencia) > 0) THEN 'S' ELSE 'N' END ie_encontrou_registro,
				coalesce(max(ie_regra_bloqueio),'X'),
				sum(nr_pos_final - nr_pos_inicial),
				max(ds_observacao)
		into STRICT 	ie_encontrou_registro_w,
				ie_regra_bloqueio_w,
				qt_tamanho_w,
				ds_observacao_w
		from 	conv_regra_usuario_categ
		where  	cd_estabelecimento  = cd_estabelecimento_p
		and  	cd_convenio  = cd_convenio_p
		and  	cd_categoria  = cd_categoria_p
		and  	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w) = ie_tipo_atendimento_w
		and  	coalesce(ie_clinica, ie_clinica_w) = ie_clinica_w  LIMIT 1;
	
		if (ie_encontrou_registro_w = 'S') then
			--'N' representa que nao e da caregoria (Nao foi encontrado regra para esse codigo)
			ie_usuario_categoria_w := 'N';	
		end if;
		
	end if;

end if;
			
ie_usuario_categoria_p	:= coalesce(ie_usuario_categoria_w,'S');
ie_regra_bloqueio_p    	:= coalesce(ie_regra_bloqueio_w,'B');
ds_observacao_p    	:= ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE validate_user_category ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_usuario_convenio_p text, ie_clinica_p bigint, ie_param_1219 text, ie_usuario_categoria_p INOUT text, ie_regra_bloqueio_p INOUT text, ds_observacao_p INOUT text) FROM PUBLIC;

